import React, { Component } from "react";

class EventBind extends Component {
  constructor(props) {
    super(props);

    this.state = {
      message: "Hello",
    };
    this.clickHandler = this.clickHandler.bind(this); //using bind in constryctor
  }

  clickHandler() {   
    this.setState({
      message: "bye",
    });
  }
  render() {
    return (
      <div>
        <div>{this.state.message}</div>
        {/* <button onClick={this.clickHandler.bind(this)}>clickBind</button>  //using bind in render method
        <button onClick={() => this.clickHandler()}>Click</button>        //  using arrow   */}    
        <button onClick={this.clickHandler}>click</button>
      </div>
    );
  }
}

export default EventBind;
